    REMOVED = 0x05;
    ADDED_ONLINE = 0x06;
    ADDED_OFFLINE = 0x07;
    ALREADY = 0x08;
    SELF = 0x09;
    ENEMY = 0x0A;
    IGNORE_FULL = 0x0B;
    IGNORE_SELF = 0x0C;
    IGNORE_NOT_FOUND = 0x0D;
    IGNORE_ALREADY = 0x0E;
    IGNORE_ADDED = 0x0F;
