  1→import { ObjectType } from "wow/protocol/entity-fields";
  2→
  3→export type Position = {
  4→  mapId: number;
  5→  x: number;
  6→  y: number;
  7→  z: number;
  8→  orientation: number;
  9→};
 10→
 11→export type BaseEntity = {
 12→  guid: bigint;
 13→  objectType: ObjectType;
 14→  entry: number;
 15→  scale: number;
 16→  position: Position | undefined;
 17→  rawFields: Map<number, number>;
 18→  name: string | undefined;
 19→};
 20→
 21→export type UnitEntity = BaseEntity & {
 22→  objectType: ObjectType.UNIT | ObjectType.PLAYER;
 23→  health: number;
 24→  maxHealth: number;
 25→  level: number;
 26→  factionTemplate: number;
 27→  displayId: number;
 28→  npcFlags: number;
 29→  unitFlags: number;
 30→  target: bigint;
 31→  race: number;
 32→  class_: number;
 33→  gender: number;
 34→  power: number[];
 35→  maxPower: number[];
 36→};
 37→
 38→export type GameObjectEntity = BaseEntity & {
 39→  objectType: ObjectType.GAMEOBJECT;
 40→  displayId: number;
 41→  flags: number;
 42→  gameObjectType: number;
 43→  bytes1: number;
 44→};
 45→
 46→export type Entity = UnitEntity | GameObjectEntity | BaseEntity;
 47→
 48→export type EntityEvent =
 49→  | { type: "appear"; entity: Entity }
 50→  | { type: "disappear"; guid: bigint; name?: string }
 51→  | { type: "update"; entity: Entity; changed: string[] };
 52→
 53→function createBase(guid: bigint, objectType: ObjectType): BaseEntity {
 54→  return {
 55→    guid,
 56→    objectType,
 57→    entry: 0,
 58→    scale: 0,
 59→    position: undefined,
 60→    rawFields: new Map(),
 61→    name: undefined,
 62→  };
 63→}
 64→
 65→function createUnit(
 66→  guid: bigint,
 67→  objectType: ObjectType.UNIT | ObjectType.PLAYER,
 68→): UnitEntity {
 69→  return {
 70→    ...createBase(guid, objectType),
 71→    objectType,
 72→    health: 0,
 73→    maxHealth: 0,
 74→    level: 0,
 75→    factionTemplate: 0,
 76→    displayId: 0,
 77→    npcFlags: 0,
 78→    unitFlags: 0,
 79→    target: 0n,
 80→    race: 0,
 81→    class_: 0,
 82→    gender: 0,
 83→    power: [0, 0, 0, 0, 0, 0, 0],
 84→    maxPower: [0, 0, 0, 0, 0, 0, 0],
 85→  };
 86→}
 87→
 88→function createGameObject(guid: bigint): GameObjectEntity {
 89→  return {
 90→    ...createBase(guid, ObjectType.GAMEOBJECT),
 91→    objectType: ObjectType.GAMEOBJECT,
 92→    displayId: 0,
 93→    flags: 0,
 94→    gameObjectType: 0,
 95→    bytes1: 0,
 96→  };
 97→}
 98→
 99→type EntityFields = Partial<
100→  Omit<UnitEntity, "objectType"> & Omit<GameObjectEntity, "objectType">
101→>;
102→
103→export class EntityStore {
104→  private entities: Map<bigint, Entity>;
105→  private byType: Map<number, Set<bigint>>;
106→  private listener?: (event: EntityEvent) => void;
107→
108→  constructor() {
109→    this.entities = new Map();
110→    this.byType = new Map();
111→  }
112→
113→  onEvent(cb: (event: EntityEvent) => void): void {
114→    this.listener = cb;
115→  }
116→
117→  create(guid: bigint, objectType: ObjectType, fields: EntityFields): void {
