 31→  | { type: "say"; message: string }
 32→  | { type: "yell"; message: string }
 33→  | { type: "guild"; message: string }
 34→  | { type: "party"; message: string }
 35→  | { type: "whisper"; target: string; message: string }
 36→  | { type: "read" }
 37→  | { type: "read_json" }
 38→  | { type: "read_wait"; ms: number }
 39→  | { type: "read_wait_json"; ms: number }
 40→  | { type: "stop" }
 41→  | { type: "status" }
 42→  | { type: "who"; filter?: string }
 43→  | { type: "who_json"; filter?: string }
 44→  | { type: "invite"; target: string }
 45→  | { type: "kick"; target: string }
 46→  | { type: "leave" }
 47→  | { type: "leader"; target: string }
 48→  | { type: "accept" }
