 51→  | { type: "nearby_json" }
 52→  | { type: "unimplemented"; feature: string };
 53→
 54→export function parseIpcCommand(line: string): IpcCommand | undefined {
 55→  if (line.startsWith("/")) {
 56→    const parsed = parseCommand(line);
 57→    switch (parsed.type) {
 58→      case "say":
 59→      case "yell":
 60→      case "guild":
 61→      case "party":
 62→        return parsed;
 63→      case "whisper":
 64→        return parsed;
 65→      case "who":
 66→        return parsed.target
 67→          ? { type: "who", filter: parsed.target }
 68→          : { type: "who" };
 69→      case "invite":
 70→      case "kick":
 71→      case "leave":
 72→      case "leader":
 73→      case "accept":
 74→      case "decline":
 75→        return parsed;
 76→      case "unimplemented":
 77→        return parsed;
 78→      default:
 79→        return { type: "say", message: line };
 80→    }
 81→  }
 82→
 83→  const spaceIdx = line.indexOf(" ");
 84→  const verb = spaceIdx === -1 ? line : line.slice(0, spaceIdx);
 85→  const rest = spaceIdx === -1 ? "" : line.slice(spaceIdx + 1);
 86→
 87→  switch (verb) {
 88→    case "SAY":
 89→    case "YELL":
 90→    case "GUILD":
 91→    case "PARTY":
 92→      return {
 93→        type: verb.toLowerCase() as "say" | "yell" | "guild" | "party",
 94→        message: rest,
 95→      };
 96→    case "WHISPER": {
 97→      const targetEnd = rest.indexOf(" ");
 98→      if (targetEnd === -1)
 99→        return { type: "whisper", target: rest, message: "" };
100→      return {
101→        type: "whisper",
102→        target: rest.slice(0, targetEnd),
103→        message: rest.slice(targetEnd + 1),
104→      };
105→    }
106→    case "READ":
107→      return { type: "read" };
108→    case "READ_JSON":
109→      return { type: "read_json" };
110→    case "READ_WAIT": {
111→      const ms = parseInt(rest, 10);
112→      if (!Number.isFinite(ms) || ms < 0) return undefined;
113→      return { type: "read_wait", ms: Math.min(ms, 60_000) };
114→    }
115→    case "READ_WAIT_JSON": {
116→      const ms = parseInt(rest, 10);
117→      if (!Number.isFinite(ms) || ms < 0) return undefined;
118→      return { type: "read_wait_json", ms: Math.min(ms, 60_000) };
119→    }
120→    case "STOP":
121→      return { type: "stop" };
122→    case "STATUS":
123→      return { type: "status" };
