257→export function formatGroupEvent(event: GroupEvent): string | undefined {
258→  switch (event.type) {
259→    case "invite_received":
260→      return `[group] ${event.from} invites you to a group`;
261→    case "command_result": {
262→      const verb =
263→        event.operation === PartyOperation.UNINVITE
264→          ? "kick"
265→          : event.operation === PartyOperation.LEAVE
266→            ? "leave"
267→            : "invite";
268→      const label =
269→        event.result === PartyResult.SUCCESS
270→          ? verb === "kick"
271→            ? `Removed ${event.target} from group`
272→            : verb === "leave"
273→              ? "Left the group"
274→              : `Invited ${event.target}`
275→          : `Cannot ${verb}${event.target ? ` ${event.target}` : ""}: ${partyResultLabel(event.result)}`;
276→      return `[group] ${label}`;
277→    }
278→    case "leader_changed":
279→      return `[group] ${event.name} is now the group leader`;
280→    case "group_destroyed":
281→      return "[group] Group has been disbanded";
282→    case "kicked":
283→      return "[group] You have been removed from the group";
284→    case "invite_declined":
