292→export function formatEntityEvent(event: EntityEvent): string | undefined {
293→  switch (event.type) {
294→    case "appear": {
295→      const e = event.entity;
296→      if (!e.name) return undefined;
297→      if (
298→        e.objectType === ObjectType.UNIT ||
299→        e.objectType === ObjectType.PLAYER
300→      ) {
301→        const unit = e as UnitEntity;
302→        const kind = e.objectType === ObjectType.PLAYER ? "Player" : "NPC";
303→        const levelStr = unit.level > 0 ? `, level ${unit.level}` : "";
304→        return `[world] ${e.name} appeared (${kind}${levelStr})`;
305→      }
306→      if (e.objectType === ObjectType.GAMEOBJECT) {
307→        return `[world] ${e.name} appeared (GameObject)`;
308→      }
309→      return undefined;
310→    }
311→    case "disappear": {
312→      const name = event.name ?? "Unknown entity";
313→      return `[world] ${name} left range`;
314→    }
315→    case "update": {
316→      if (!event.changed.includes("name") || !event.entity.name)
317→        return undefined;
318→      return formatEntityEvent({ type: "appear", entity: event.entity });
319→    }
320→  }
