362→export type TuiState = {
363→  handle: WorldHandle;
364→  write: (s: string) => void;
365→  lastWhisperFrom: string | undefined;
366→  showEntityEvents: boolean;
367→};
368→
369→export async function executeCommand(
370→  state: TuiState,
371→  cmd: Command,
372→): Promise<boolean> {
373→  switch (cmd.type) {
374→    case "chat":
375→      state.handle.sendInCurrentMode(cmd.message);
376→      break;
377→    case "say":
378→      state.handle.sendSay(cmd.message);
379→      break;
380→    case "yell":
381→      state.handle.sendYell(cmd.message);
382→      break;
383→    case "guild":
384→      state.handle.sendGuild(cmd.message);
385→      break;
386→    case "party":
387→      state.handle.sendParty(cmd.message);
388→      break;
389→    case "raid":
390→      state.handle.sendRaid(cmd.message);
391→      break;
392→    case "whisper":
393→      state.handle.sendWhisper(cmd.target, cmd.message);
394→      state.lastWhisperFrom = cmd.target;
395→      break;
396→    case "reply":
397→      if (!state.lastWhisperFrom) {
398→        state.write(formatError("No one has whispered you yet.") + "\n");
399→      } else {
400→        state.handle.sendWhisper(state.lastWhisperFrom, cmd.message);
401→      }
402→      break;
403→    case "channel": {
404→      const channel = /^\d+$/.test(cmd.target)
405→        ? state.handle.getChannel(parseInt(cmd.target, 10))
406→        : cmd.target;
407→      if (!channel) {
408→        state.write(formatError(`Not in channel ${cmd.target}.`) + "\n");
409→      } else {
410→        state.handle.sendChannel(channel, cmd.message);
411→      }
412→      break;
413→    }
414→    case "who": {
415→      const results = await state.handle.who(
416→        cmd.target ? { name: cmd.target } : {},
417→      );
418→      state.write(formatWhoResults(results) + "\n");
419→      break;
420→    }
421→    case "invite":
422→      state.handle.invite(cmd.target);
423→      break;
424→    case "kick":
425→      state.handle.uninvite(cmd.target);
426→      break;
427→    case "leave":
428→      state.handle.leaveGroup();
429→      break;
430→    case "leader":
431→      state.handle.setLeader(cmd.target);
432→      break;
433→    case "accept":
434→      state.handle.acceptInvite();
435→      break;
436→    case "decline":
437→      state.handle.declineInvite();
438→      break;
439→    case "quit":
440→      return true;
441→    case "tuicraft":
442→      if (cmd.subcommand === "entities") {
443→        if (cmd.value === "on") {
444→          state.showEntityEvents = true;
445→          state.write("[system] Entity events enabled\n");
446→        } else if (cmd.value === "off") {
447→          state.showEntityEvents = false;
448→          state.write("[system] Entity events disabled\n");
449→        } else {
450→          state.write("[system] Usage: /tuicraft entities on|off\n");
451→        }
452→      } else {
453→        state.write(`[system] Unknown tuicraft command: ${cmd.subcommand}\n`);
454→      }
455→      break;
456→    case "unimplemented":
457→      state.write(formatError(`${cmd.feature} is not yet implemented`) + "\n");
458→      break;
459→  }
460→  return false;
461→}
462→
463→export type TuiOptions = {
464→  input?: NodeJS.ReadableStream;
465→  write?: (s: string) => void;
466→};
467→
468→export function startTui(
469→  handle: WorldHandle,
470→  interactive: boolean,
471→  opts: TuiOptions = {},
472→): Promise<void> {
473→  const write = opts.write ?? ((s: string) => void process.stdout.write(s));
474→  const state: TuiState = {
475→    handle,
476→    write,
477→    lastWhisperFrom: undefined,
478→    showEntityEvents: false,
479→  };

