  1→import { readConfig } from "lib/config";
  2→import { authHandshake, authWithRetry, worldSession } from "wow/client";
  3→import type { WorldHandle } from "wow/client";
  4→import { RingBuffer } from "lib/ring-buffer";
  5→import { socketPath, pidPath, runtimeDir, logPath } from "lib/paths";
  6→import { SessionLog } from "lib/session-log";
  7→import { mkdir, writeFile, unlink } from "node:fs/promises";
  8→import {
  9→  parseIpcCommand,
 10→  dispatchCommand,
 11→  onChatMessage,
 12→  onGroupEvent,
 13→  onEntityEvent,
 14→  writeLines,
 15→  type EventEntry,
 16→  type IpcSocket,
 17→} from "daemon/commands";
 18→
 19→type SocketState = { buffer: string; processing: boolean; ended: boolean };
 20→
 21→type ServerCtx = {
 22→  handle: WorldHandle;
 23→  events: RingBuffer<EventEntry>;
 24→  cleanup: () => void;
 25→  onActivity?: () => void;
 26→  onStop?: () => void;
 27→};
 28→
 29→type DaemonServerArgs = {
 30→  handle: WorldHandle;
 31→  sock: string;
 32→  log: SessionLog;
 33→  onActivity?: () => void;
 34→  onStop?: () => void;
 35→};
 36→
 37→type DaemonServer = {
 38→  server: ReturnType<typeof Bun.listen>;
 39→  events: RingBuffer<EventEntry>;
 40→  cleanup: () => void;
 41→};
 42→
 43→type DaemonClient = {
 44→  authHandshake: typeof authHandshake;
 45→  worldSession: typeof worldSession;
 46→};
 47→
 48→const socketStates = new WeakMap<IpcSocket, SocketState>();
 49→
 50→function getSocketState(socket: IpcSocket): SocketState {
 51→  const existing = socketStates.get(socket);
 52→  if (existing) return existing;
 53→  const state: SocketState = { buffer: "", processing: false, ended: false };
 54→  socketStates.set(socket, state);
 55→  return state;
 56→}
 57→
 58→function drainNextLine(ctx: ServerCtx, socket: IpcSocket): void {
 59→  const state = getSocketState(socket);
 60→  state.processing = false;
 61→  if (state.ended) return;
 62→  const nextBreak = state.buffer.indexOf("\n");
 63→  if (nextBreak !== -1) {
 64→    const next = state.buffer.slice(0, nextBreak).trim();
 65→    state.buffer = state.buffer.slice(nextBreak + 1);
 66→    processLine(ctx, socket, next);
 67→    return;
 68→  }
 69→  state.ended = true;
 70→  socket.end();
 71→}
 72→
 73→function processLine(ctx: ServerCtx, socket: IpcSocket, line: string): void {
 74→  const state = getSocketState(socket);
 75→  if (state.processing || state.ended) return;
 76→  state.processing = true;
 77→  ctx.onActivity?.();
 78→  const cmd = parseIpcCommand(line);
 79→  if (!cmd) {
 80→    writeLines(socket, ["ERR unknown command"]);
 81→    state.ended = true;
 82→    state.processing = false;
 83→    socket.end();
 84→    return;
 85→  }
 86→  dispatchCommand(cmd, ctx.handle, ctx.events, socket, ctx.cleanup)
 87→    .then((shouldExit) => {
 88→      if (shouldExit) (ctx.onStop ?? (() => process.exit(0)))();
 89→    })
 90→    .catch(() => writeLines(socket, ["ERR internal"]))
 91→    .finally(() => drainNextLine(ctx, socket));
 92→}
 93→
 94→function onSocketData(ctx: ServerCtx, socket: IpcSocket, data: Buffer): void {
 95→  const state = getSocketState(socket);
 96→  state.buffer += Buffer.from(data).toString();
 97→  const breakIdx = state.buffer.indexOf("\n");
 98→  if (breakIdx === -1) return;
 99→  const line = state.buffer.slice(0, breakIdx).trim();
100→  state.buffer = state.buffer.slice(breakIdx + 1);
101→  processLine(ctx, socket, line);
102→}
103→
104→export function startDaemonServer(args: DaemonServerArgs): DaemonServer {
105→  const { handle, sock, log, onActivity, onStop } = args;
106→  const events = new RingBuffer<EventEntry>(1000);
107→  handle.onMessage((msg) => onChatMessage(msg, events, log));
108→  handle.onGroupEvent((event) => onGroupEvent(event, events, log));
109→  handle.onEntityEvent((event) => onEntityEvent(event, events, log));
110→
111→  let cleaned = false;
112→  function cleanup(): void {
113→    if (cleaned) return;
114→    cleaned = true;
115→    handle.close();
116→    server.stop();
117→    unlink(sock).catch(() => {});
118→  }
119→
120→  const ctx: ServerCtx = { handle, events, cleanup, onActivity, onStop };
121→  const server = Bun.listen({
122→    unix: sock,
123→    socket: { data: (socket, data) => onSocketData(ctx, socket, data) },
124→  });
125→
126→  return { server, events, cleanup };
127→}
128→
129→function buildClientConfig(cfg: Awaited<ReturnType<typeof readConfig>>) {
130→  return {
131→    host: cfg.host,
132→    port: cfg.port,
133→    account: cfg.account.toUpperCase(),
134→    password: cfg.password.toUpperCase(),
135→    character: cfg.character,
136→    language: cfg.language,
