import { PacketReader, PacketWriter } from "wow/protocol/packet";

export const SocialFlag = {
  FRIEND: 0x01,
  IGNORED: 0x02,
  MUTED: 0x04,
} as const;

export const FriendStatus = {
  OFFLINE: 0,
  ONLINE: 1,
  AFK: 2,
  DND: 4,
} as const;

export const FriendResult = {
  DB_ERROR: 0x00,
  LIST_FULL: 0x01,
  ONLINE: 0x02,
  OFFLINE: 0x03,
  NOT_FOUND: 0x04,
  REMOVED: 0x05,
  ADDED_ONLINE: 0x06,
  ADDED_OFFLINE: 0x07,
  ALREADY: 0x08,
  SELF: 0x09,
  ENEMY: 0x0a,
} as const;

export function buildAddFriend(name: string, note: string): Uint8Array {
  const w = new PacketWriter();
  w.cString(name);
  w.cString(note);
  return w.finish();
}

export function buildDelFriend(guid: bigint): Uint8Array {
  const w = new PacketWriter();
  w.uint64LE(guid);
  return w.finish();
}
