 1→export type FriendEntry = {
 2→  guid: bigint;
 3→  name: string;
 4→  note: string;
 5→  status: number;
 6→  area: number;
 7→  level: number;
 8→  playerClass: number;
 9→};
10→
11→export type FriendEvent =
12→  | { type: "friend-list"; friends: FriendEntry[] }
13→  | { type: "friend-online"; friend: FriendEntry }
14→  | { type: "friend-offline"; guid: bigint; name: string }
15→  | { type: "friend-added"; friend: FriendEntry }
16→  | { type: "friend-removed"; guid: bigint; name: string }
17→  | { type: "friend-error"; result: number; name: string };
18→
19→type FriendUpdateFields = Partial<
20→  Pick<FriendEntry, "status" | "area" | "level" | "playerClass">
21→>;
22→
23→export class FriendStore {
24→  private friends = new Map<bigint, FriendEntry>();
25→  private listener?: (event: FriendEvent) => void;
26→
27→  onEvent(cb: (event: FriendEvent) => void): void {
28→    this.listener = cb;
29→  }
30→
31→  set(entries: FriendEntry[]): void {
32→    this.friends.clear();
33→    for (const entry of entries) {
34→      this.friends.set(entry.guid, { ...entry });
35→    }
36→    this.listener?.({
37→      type: "friend-list",
38→      friends: [...this.friends.values()],
39→    });
40→  }
41→
42→  add(entry: FriendEntry): void {
43→    const copy = { ...entry };
44→    this.friends.set(copy.guid, copy);
45→    this.listener?.({ type: "friend-added", friend: copy });
46→  }
47→
48→  update(guid: bigint, fields: FriendUpdateFields): void {
49→    const entry = this.friends.get(guid);
50→    if (!entry) return;
51→
52→    Object.assign(entry, fields);
53→
54→    if (entry.status === 0) {
55→      this.listener?.({ type: "friend-offline", guid, name: entry.name });
56→    } else {
57→      this.listener?.({ type: "friend-online", friend: entry });
58→    }
59→  }
60→
61→  remove(guid: bigint): void {
62→    const entry = this.friends.get(guid);
