): void {
  const text = formatFriendEvent(event);
  const obj = formatFriendEventObj(event);
  if (obj) {
    events.push({ text: text ?? undefined, json: JSON.stringify(obj) });
    log.append(obj as LogEntry).catch(() => {});
  }
}

