describe("friend list", () => {
  function buildContactList(
    entries: {
      guid: bigint;
      flags: number;
      note: string;
      status?: number;
      area?: number;
      level?: number;
      playerClass?: number;
    }[],
  ): Uint8Array {
    const w = new PacketWriter();
    w.uint32LE(7);
    w.uint32LE(entries.length);
    for (const e of entries) {
      w.uint64LE(e.guid);
      w.uint32LE(e.flags);
      w.cString(e.note);
      if (e.flags & 0x01) {
        const status = e.status ?? 0;
        w.uint8(status);
        if (status !== 0) {
          w.uint32LE(e.area ?? 0);
          w.uint32LE(e.level ?? 0);
          w.uint32LE(e.playerClass ?? 0);
        }
      }
    }
    return w.finish();
  }

  function buildFriendStatus(opts: {
    result: number;
    guid: bigint;
    note?: string;
    status?: number;
    area?: number;
    level?: number;
    playerClass?: number;
  }): Uint8Array {
    const w = new PacketWriter();
    w.uint8(opts.result);
    w.uint64LE(opts.guid);
    if (opts.result === 0x06 || opts.result === 0x07) {
      w.cString(opts.note ?? "");
    }
    if (opts.result === 0x06 || opts.result === 0x02) {
      w.uint8(opts.status ?? 1);
      w.uint32LE(opts.area ?? 0);
      w.uint32LE(opts.level ?? 0);
      w.uint32LE(opts.playerClass ?? 0);
    }
    return w.finish();
  }

  function buildNameQueryResponse(guidLow: number, name: string): Uint8Array {
    const w = new PacketWriter();
    const mask = guidLow === 0 ? 0 : guidLow <= 0xff ? 0x01 : 0x03;
    w.uint8(mask);
    if (mask & 0x01) w.uint8(guidLow & 0xff);
    if (mask & 0x02) w.uint8((guidLow >> 8) & 0xff);
    w.uint8(0);
    w.cString(name);
    w.cString("");
    w.uint32LE(1);
    w.uint32LE(0);
    w.uint32LE(1);
    return w.finish();
  }

